# 方法1 正常Python人思维
def generate_pwd_list(dic, max_len):
    """
    description:生成指定长度的密码序列
    param {*} dic   字典
    param {*} pwd_len   最大密码长度
    return {*}
    """
    k = itertools.product(dic, repeat=max_len)  # 迭代器
    allkey = ("".join(i) for i in k)
    if max_len == 1:
        return list(allkey)
    return generate_pwd_list(dic, max_len - 1) + list(allkey)


