# 方法2 递归
def combi(seq):
    if not seq:
        yield []
    else:
        for element in seq[0]:
            for rest in combi(seq[1:]):
                yield [element] + rest


def sets(dic, pwd_len):
    n = []
    for i in range(pwd_len):
        n.append(dic)
    return list(combi(n))


